* @Author: hao.ling
* @Date: 2020/8/1 1:27 下午
* @Explanation: 查询用户
<template>
    <div id="search">
        <h1 align="center">重置密码</h1>
        <a-form :form="forget_form" :label-col="{span:7}" @submit="nextStep">
            <a-form-item label="用户名" class="forget-form-item">
                <a-input class="input" size="small" placeholder="请输入用户名"
                         v-decorator="['username',{rules:[{required:true,message:'请输入用户名'}]}]">
                    <my-icon slot="prefix" type="icon-user" class="icon"/>
                </a-input>
            </a-form-item>
            <a-button type="primary" class="btn" style="margin-left: 100px">
                <router-link :to="{name:'login'}">取消</router-link>
            </a-button>
            <a-button type="primary" class="btn" @click="nextStep">下一步</a-button>
        </a-form>
    </div>
</template>

<script>
    export default {
        name: "searchUser",
        data() {
            return {
                forget_form: this.$form.createForm(this, {name: "forgetForm"}),
            }
        },
        methods: {
            // 下一步校验用户名
            nextStep(e) {
                e.preventDefault()
                this.forget_form.validateFields((err, value) => {
                    if (!err) {
                        this.Get("/searchUser", {username: value['username']}).then(res => {
                            res['code'] === 1000 ? this.$message.success(res['message'], 1, () => {
                                this.$router.push({name: "forgetPwd", params: {username: value['username']}})
                            }) : this.$message.error(res['message'])
                        })
                    }
                })
            },
        }
    }
</script>

<style scoped>
    #search {
        margin-top: 120px;
        margin-left: 500px;
        padding-top: 20px;
        height: 350px;
        width: 400px;
        background-color: white;
        border-radius: 20px;
    }

    .btn {
        margin-top: 20px;
        margin-right: 50px;
    }
</style>
<style>
    .forget-form-item .ant-form-explain {
        margin-left: 120px;
    }
</style>
